import React from "react";
import "./myaccount.scss";
import { useSelector } from "react-redux";
import Navbar from "components/Navbar/Navbar";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import GivenOffers from "components/GivenOffers/GivenOffers";
import GottenOffers from "components/GottenOffers/GottenOffers";

function MyAccount() {
  const userAccount = useSelector((state) => state.auth.userMail);

  console.log(userAccount);
  return (
    <div className="myaccount-wrapper">
      <Router>
        <Navbar />
        <div className="mail-adress-container">
          <div className="mail-adress-area">
            <i className="far fa-user" />
            <h3>{userAccount}</h3>
          </div>
        </div>

        <div className="order-menu-wrapper">
          <div className="order-menu-container">
            <Link to="/gottenoffers">Teklif Aldıklarım</Link>
            <Link to="/givenoffers">Teklif Verdiklerim</Link>
          </div>
        </div>
        {/* <OrderedItems /> */}
        <Switch>
          <Route exact path="/myaccount/gottenoffers" component={GottenOffers} />

          <Route exact path="/myaccount/givenoffers" component={GivenOffers} />
        </Switch>
      </Router>
    </div>
  );
}

export default MyAccount;
