/* eslint-disable dot-notation */
/* eslint-disable no-multi-assign */
/* eslint-disable no-undef */
import React, { useState, useEffect } from "react";
import "./productDetails.scss";
import { useParams } from "react-router-dom";
import Navbar from "components/Navbar/Navbar";
import axios from "../../../node_modules/axios/index";

function ProductDetails() {
  let { productDetailId } = useParams();
  const [getProduct, setGetProduct] = useState({});
  const [brandInfo, setBrandInfo] = useState({});

  // Fetching asyncroniously id entered data in order to show it Product Details Page
  const getProductData = async () => {
    await axios
      .get(`http://bootcampapi.techcs.io/api/fe/v1/product/${productDetailId}`)
      .then((data1) => setGetProduct(data1.data))
      .catch((error) => console.log(error));
  };

  // Mounting getProductData() function when get data productDetailId drilled from parent component
  useEffect(() => {
    getProductData();
  }, [productDetailId]);

  console.log("getProduct: ", getProduct);
  console.log(brandInfo);

  return (
    <div className="home-wrapper">
      <div className="home-container">
        <Navbar />
      </div>
      <div className="product-info-wrapper">
        <div className="product-info-container">
          {/* Cover Picture */}
          <div className="left-product-cover">
            <img src={getProduct.imageUrl} alt={getProduct.title} />
          </div>

          {/* Right Product Info */}
          <div className="right-product-info">
            <h2>{getProduct.title}</h2>
            <div>
              <span>
                <b>Marka:</b>
              </span>
              <span>{brandInfo.title}</span>
            </div>
            <div>
              <span>
                <b>Renk:</b>
              </span>
              <span>test2</span>
            </div>
            <div>
              <span>
                <b>Kullanım Durumu:</b>
              </span>
              <span>test3</span>
            </div>
            <h2>Price</h2>
            <div>
              <button type="submit">Satın Al</button>
              <button type="submit">Teklif Ver</button>
            </div>
            <h3>Açıklama</h3>
            <p>Burası apiden çekilecek. Unutma.Lorem ipsum dolor sit amet.</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProductDetails;
