import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { Link, useRouteMatch, Switch, Route } from "react-router-dom";
import "./categories.scss";
import Products from "components/Products/Products";
import { getCategories } from "../../redux/actions/getCategoryAction";

function Categories() {
  const categories = useSelector((state) => state.category.categories.data);
  const dispatch = useDispatch();
  let { path, url } = useRouteMatch();

  console.log({ categories });
  useEffect(() => {
    dispatch(getCategories());
  }, []);

  console.log("path:", path);
  console.log("url:", url);
  return (
    <div className="category-container">
      {/* Listing All Categories */}
      <div className="category-wrapper">
        {categories &&
          categories.map((categoryItem) => (
            <div key={categoryItem.id} className="category-item">
              <Link
                className="category-link"
                to={`${url}/${categoryItem.title}`}
              >{`${categoryItem.title.charAt(0).toUpperCase()}${categoryItem.title.slice(
                1
              )}`}</Link>
            </div>
          ))}
      </div>

      {/* Nesting for Products */}
      <Switch>
        <Route path={`${path}`}>
          <Products />
        </Route>
      </Switch>
    </div>
  );
}

export default Categories;
