import React, { useEffect } from "react";
import "./products.scss";
import { useSelector, useDispatch } from "react-redux";
import { getProducts } from "../../redux/actions/productsActions";

function Products() {
  const products = useSelector((state) => state.products.products.data);
  const dispatch = useDispatch();

  const letterChanger = (firstCharacter, restCharacter) => {
    return `${firstCharacter.charAt(0).toUpperCase}${restCharacter.slice(1)}`;
  };

  useEffect(() => {
    dispatch(getProducts());
    console.log("products", { products });
  }, []);

  return (
    <div className="card-top-container">
      <div className="card-wrapper">
        {products &&
          products.map((productItem) => (
            <div key={productItem.id} className="card-container">
              <img
                src={productItem.imageUrl}
                alt={productItem.brand.title}
                className="card-image"
              />
              <div className="card-product-informartion">
                <span>
                  <b>{letterChanger(productItem.brand.title, productItem.brand.title)()}</b>
                </span>
                <span>
                  <b>Renk:</b>
                  {`${productItem.color.title.charAt(0).toUpperCase()}`}
                </span>
              </div>
              <div className="card-product-price">{productItem.price} TL</div>
            </div>
          ))}
      </div>
    </div>
  );
}

export default Products;
