/* eslint-disable react/button-has-type */
// import "./receivedoffer.scss";
import React, { useEffect, useRef } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getReceivedOffers } from "redux/actions/receivedOffers";
import ModalSecond from "components/ModalSecond/ModalSecond";
import "./receivedoffers.scss";
// import "../GivenOffers/givenoffers.scss";

function ReceivedOffers() {
  const receivedOfferItems = useSelector((state) => state.receivedOffers.receivedOffers.data);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getReceivedOffers());
  }, []);
  const modalSecondRef = useRef();
  const openModalSecond = () => {
    modalSecondRef.current.modalSecondOpener();
  };
  const closeModalSecond = () => {
    modalSecondRef.current.modalSecondCloser();
  };
  const purchaseHandler = (e) => {
    e.preventDefault();
    // dispatch(getPurchasedItem());
    closeModalSecond();
    // notifyPurchaseSuccess("Satın Alındı");
  };

  console.log(receivedOfferItems);
  return (
    <div>
      {receivedOfferItems?.length === 0 && <h1> Henüz bir teklif almadınız.</h1>}

      <div className="given-offers-wrapper">
        {receivedOfferItems?.map((receivedOffer) => (
          <div className="given-offer-card" key={receivedOffer.id}>
            <div className="left-side">
              <div className="given-offer-img">
                <img
                  className="given-offer-full-image"
                  src={receivedOffer?.product?.imageUrl}
                  alt=""
                />
              </div>
              <div className="given-offer-info">
                <span className="given-offer-title">{receivedOffer?.product?.title}</span>
                <span className="received-info-gray">
                  <b>{receivedOffer?.offeredPrice} TL</b>
                </span>
              </div>
            </div>
            <div className="right-side">
              <div className="given-offer-result">
                {receivedOffer?.isSold === "sold" && (
                  <span className="offer-rejected">Reddedildi</span>
                )}
                {!receivedOffer?.isSold && (
                  <div>
                    <button
                      onClick={() => {
                        openModalSecond();
                      }}
                    >
                      Satın Al
                    </button>
                    <span className="offer-accepted">Onaylandı</span>
                    <ModalSecond ref={modalSecondRef}>
                      <form className="purchase-container">
                        <h3 className="purchase-title">SATIN AL</h3>
                        <p>Satın almak istiyor musunuz ?</p>
                        <div>
                          <button className="reject" onClick={closeModalSecond}>
                            Vazgeç
                          </button>
                          <button className="accept" type="submit" onClick={purchaseHandler}>
                            Satın Al
                          </button>
                        </div>
                      </form>
                    </ModalSecond>
                  </div>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ReceivedOffers;
