import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import "./categories.scss";
import { getCategories } from "../../redux/actions/getCategoryAction";

function Categories() {
  const categories = useSelector((state) => state.category.categories.data);
  const dispatch = useDispatch();

  console.log({ categories });
  useEffect(() => {
    dispatch(getCategories());
  }, []);

  return (
    <div className="category-container">
      <div className="category-wrapper">
        {categories.map((newCategory) => (
          <div key={newCategory.id} className="category">
            <Link to={`/${newCategory.title}`}>{newCategory.title}</Link>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Categories;
