import * as types from "redux/actionTypes/actionTypes";

const initialState = {
  // title: "",
  // id: "",
  info: {},
  loading: false,
};

const getCategoryReducer = (state = initialState, action) => {
  switch (action.type) {
    case types.GET_CATEGORY_START:
      return { ...state, loading: true };
    case types.GET_CATEGORY:
      return { ...state, info: action.payload, loading: false };
    default:
      return state;
  }
};

export default getCategoryReducer;
