/* eslint-disable prefer-const */
import "./login.scss";

import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { loginInitiate } from "redux/actions";
import { useHistory, Link } from "react-router-dom";
import registerGirl from "../../images/registerGirl.png";
import ikincielLogo from "../../images/ikincielLogo.svg";

function Login() {
  const [mail, setMail] = useState("");
  const [password, setPassword] = useState("");
  const history = useHistory();
  let dispatch = useDispatch();

  let { user, error } = useSelector((state) => state.auth);
  useEffect(() => {
    {
      user && history.push("/");
    }
    console.log({ user });
    console.log({ error });
  }, [user]);

  // onSubmitHandler will submit taken data from input and Redirect to HOME Page
  const onSubmitHandler = (e) => {
    e.preventDefault();
    dispatch(loginInitiate(mail, password));
  };

  // this is going to return entered value into input
  const onChangeHandler = (e) => {
    let input = e.target.value;
    {
      e.target.type === "email" ? setMail(input) : setPassword(input);
    }
  };

  return (
    <>
      {/* LOGIN OLD Version */}
      <h1>GİRİŞ YAP SAYFASI</h1>
      <div className="login-container">
        {/* Left Side Image */}
        <div>
          <img
            src="https://i.picsum.photos/id/871/200/300.jpg?hmac=wXN1u0NeBnK8vCkjkJXzoTfZn6F0JBzgOpCdmRGXsw0"
            alt="dummy"
          />
        </div>
        {/* Right Side - Input Form */}
        <form>
          <div>
            <div>
              <label htmlFor="email">EMail</label>
            </div>
            <input type="email" name="email" id="email" onChange={onChangeHandler} />
          </div>
          <div>
            <div>
              <label htmlFor="password">Şifre</label>
            </div>
            <input type="text" name="password" id="password" onChange={onChangeHandler} />
          </div>
          <button type="submit" onClick={onSubmitHandler}>
            GİRİŞ YAP
          </button>
        </form>
      </div>

      {/* NEW VERSION */}
      <div className="signup-wrapper">
        <div className="signup">
          {/* Left Side Image */}
          <img src={registerGirl} alt="signup-girl" className="signup__image" />
          {/* Right Side - Input Form */}
          <div className="signup-form-wrapper">
            <img src={ikincielLogo} alt="ikincielLogo" />

            <form className="signup__form">
              <div className="signup__form__topDiv">
                <h3>ÜYE OL</h3>
                <small>Fırsatlardan yararlanmak için üye ol!</small>
              </div>
              <div>
                <div>
                  <label htmlFor="email">Email</label>
                </div>
                <input
                  type="email"
                  name="email"
                  id="email"
                  onChange={onChangeHandler}
                  placeholder="email@example.com"
                />
              </div>
              <div>
                <div>
                  <label htmlFor="password">Şifre</label>
                </div>
                <input type="text" name="password" id="password" onChange={onChangeHandler} />
              </div>
              <button type="submit" onClick={onSubmitHandler}>
                Üye Ol
              </button>
              <div className="signup__form__login">
                <small>
                  Hesabın var mı ?{" "}
                  <Link className="link" to="/login">
                    Giriş yap
                  </Link>
                </small>
              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}

export default Login;
