/* eslint-disable import/named */
import axios from "axios";
import * as types from "../actionTypes/actionTypes";
import { user } from "../reducers/reducer";

console.log(user);

const giveOfferPending = () => ({
  type: types.GIVE_PRODUCT_OFFER_PENDING,
});
const giveOfferSuccess = (productId, givenOfferPrice) => ({
  type: types.GIVE_PRODUCT_OFFER_SUCCESS,
  productId,
  givenOfferPrice,
});

const config = {
  headers: { "Content-Type": "application/json", Authorization: `Bearer ${user}` },
};

// Buradaki AUTH yüzünden alnımın derisi çatladı, o yüzden çalışıyorsa elleme diyerek kendimi teselli ediyorum :((
const postOfferedValue = async () => {
  await axios
    .post(`http://bootcampapi.techcs.io/api/fe/v1/product/offer/${productDetailId}`, {
      offeredPrice: 45,
    })
    .then((response) => console.log(response))
    .catch((err) => console.log(err));
};

// POST REQUEST

export const offerInitiate = (id, price) => {
  return function (dispatch) {
    dispatch(giveOfferPending());
    axios
      .post(
        `http://bootcampapi.techcs.io/api/fe/v1/product/offer/${id}`,
        { offeredPrice: price },
        config
      )
      .then((response) => {
        console.log("offered Price Response:", response);
        dispatch(giveOfferSuccess(id, price));
      })
      .catch((err) => console.log("offered price error:", err));
  };
};
