import axios from "axios";
import * as types from "../actionTypes/actionTypes";

const getCategoryStart = () => ({
  type: types.GET_CATEGORY_START,
});
const getCategorySuccess = (data) => ({
  type: types.GET_CATEGORY,
  payload: data,
});

const getCategories = () => async (category) => {
  category(getCategoryStart());
  return axios
    .get("/detail/category/all")
    .then((data) => category(getCategorySuccess(data)))
    .catch((error) => console.log("Category Error:", error));
};

export default getCategories();
