import React, { useEffect } from "react";
import "./products.scss";
import { useSelector, useDispatch } from "react-redux";
import { getProducts } from "../../redux/actions/productsActions";

function Products() {
  const products = useSelector((state) => state.products.products.data);
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getProducts());
    console.log("products", { products });
  }, []);

  return (
    <div className="card-wrapper">
      {products &&
        products.map((productItem) => (
          <div key={productItem.id} className="card-container">
            <img src={productItem.imageUrl} alt={productItem.brand.title} className="card-image" />
            <div>
              <span>{productItem.brand.title}</span>
              <span>
                <b>Renk:</b>
                {productItem.color.title}
              </span>
            </div>
            <div>{productItem.price} TL</div>
          </div>
        ))}
    </div>
  );
}

export default Products;
