import { GET_CATEGORY } from "redux/actionTypes/actionTypes";

const initialState = {
  title: "",
  id: "",
  loading: false,
};

const getCategoryReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_CATEGORY:
      return { ...state, title: action.payload.title, id: action.payload.id };
    default:
      return state;
  }
};

export default getCategoryReducer;
