import React, { useState } from "react";
import "./myaccount.scss";
import { useSelector } from "react-redux";
import Navbar from "components/Navbar/Navbar";
import { useRouteMatch, Link } from "react-router-dom";
import GottenOffers from "components/GottenOffers/GottenOffers";

function MyAccount() {
  const userAccount = useSelector((state) => state.auth.userMail);
  const [offerComponentController, setOfferComponentController] = useState(false);
  let { url } = useRouteMatch();
  return (
    <div className="myaccount-wrapper">
      <Navbar />
      <div className="mail-adress-container">
        <div className="mail-adress-area">
          <i className="far fa-user" />
          <h3>{userAccount}</h3>
        </div>
      </div>

      <div className="order-menu-wrapper">
        <div className="order-menu-container">
          <Link to={`${url}/gottenoffers`}>Teklif Aldıklarım</Link>
          <Link to={`${url}/givenoffers`}>Teklif Verdiklerim</Link>
        </div>
        <div className="offer-component-container">
          {!offerComponentController && <GottenOffers />}
        </div>
      </div>
      {/* <OrderedItems /> */}
    </div>
  );
}

export default MyAccount;
